package com.example.juncookapp.logic.CustomNetWorkimport android.app.Dialogimport android.content.BroadcastReceiverimport android.content.Contextimport android.content.Intentimport android.widget.Buttonimport android.widget.Toastclass MyReceiver : BroadcastReceiver() {    override fun onReceive(context: Context, intent: Intent) {        var status = NetWorkUtill.getConnectionStatusString(context!!)        Toast.makeText(context,status, Toast.LENGTH_SHORT).show()    }}