package com.example.juncookapp.logic.JunCookMediaimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Query@Daointerface FileDao {    @Query("SELECT * FROM files WHERE id = :id")    fun loadById(id: Long): List<FileEntity>    @Insert(onConflict = OnConflictStrategy.REPLACE)    fun insert(fileEntity: FileEntity)}