package com.example.juncookapp.logic.monitorimport android.content.BroadcastReceiverimport android.content.Contextimport android.content.Intentimport android.net.ConnectivityManagerclass ConnectivityReceiver : BroadcastReceiver() {    override fun onReceive(context: Context, intent: Intent) {        val connectivityManager =            context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager        val networkInfo = connectivityManager.activeNetworkInfo        if (networkInfo != null && networkInfo.isConnected) {            // internet connection is available        } else {            // internet connection is not available        }    }}